<?php
// Hex string sepanjang 1536 karakter (768 byte)
$hex_string = "";

// Validasi string hanya berisi hex
if (!ctype_xdigit($hex_string) || strlen($hex_string) !== 1536) {
    die("âŒ Hex string tidak valid atau tidak berjumlah 1536 karakter.");
}

// Pecah menjadi byte (2 karakter per byte)
$bytes = str_split($hex_string, 2);

echo "<pre>";
$counter = 0;
foreach ($bytes as $byte) {
    printf("0x%s", strtoupper($byte));
    $counter++;
    echo ($counter < count($bytes)) ? ", " : "";
    if ($counter % 16 === 0) echo "\n"; // baris baru tiap 16 byte
}
echo "</pre>";
?>
